            new Models.Test("1. Для чего нужны уровни Фибоначчи?", "Нахождение уроввней поддержки и сопротивления", "Сделать график красивее" , "Нахождение максимума", "Определение тренда", 1),
            new Models.Test("2. Как выглядит ввер Фибоначчи?", "Квадрат", "Крест" ,"Наклонные лининии из одной точки", "Горизонтальные линнии", 3),
            new Models.Test("3. Для чего нужно расширение Фибоначчи?", "Оценить количество активных ордеров", "Оценить шансы роста" ,"Оценить шансы падения", "Оценить масштаб ценового импульса", 4),
