            new Models.Test("3. Какие свечи называют Доджи?", "Свечи без нижней тени", "Свечи без теней" ,"Свечи без тел", "Свечи без верхней тени", 3),
            new Models.Test("4. О чем сигнализирует поглощение?", "Разворот тренда", "Продолжение тренда" ,"Аномальное падение", "Аномальный рост", 1)
