        #region Обновление курсов валют
        static public void UpdatePrices()
        {
            if (Connectivity.NetworkAccess != NetworkAccess.Internet)
                return;
            WebClient client = new WebClient();
            client.UseDefaultCredentials = true;
            string alldata = client.DownloadString("https://api.kraken.com/0/public/Ticker?pair=xbtusd,ethusd,xrpusd,ltcusd,xmrusd,eosusd");
            var x = alldata.Split('X');

            double[] prices = GetPrices(x[4], 4);
            coins[0].Lastprice = prices[0];
            coins[0].Min24 = prices[1];
            coins[0].Max24 = prices[2];

            prices = GetPrices(x[1], 4);
            coins[1].Lastprice = prices[0];
            coins[1].Min24 = prices[1];
            coins[1].Max24 = prices[2];

            prices = GetPrices(x[8], 4);
            coins[2].Lastprice = prices[0];
            coins[2].Min24 = prices[1];
            coins[2].Max24 = prices[2];

            prices = GetPrices(x[2], 4);
            coins[3].Lastprice = prices[0];
            coins[3].Min24 = prices[1];
            coins[3].Max24 = prices[2];

            prices = GetPrices(x[6], 4);
            coins[4].Lastprice = prices[0];
            coins[4].Min24 = prices[1];
            coins[4].Max24 = prices[2];

            prices = GetPrices(x[0], 4);
            coins[5].Lastprice = prices[0];
            coins[5].Min24 = prices[1];
            coins[5].Max24 = prices[2];

            Profile.UpdateProfit = true;
            Profile.UpdateOrNot = true;
            App.SaveCoins();
        }

        static double[] GetPrices(string data, int p)
        {
            double[] allprices = new double[3];

            int currentIndex = data.LastIndexOf('c') + 5;
            char i = data[currentIndex];
            string price = "";
            do
            {
                price += i;
                currentIndex++;
                i = data[currentIndex];
            } while (i != '.');
            price += '.';
            for (int j = 1; j <= p; j++)
            {
                price += data[currentIndex + j];
            }
            try
            {
                allprices[0] = double.Parse(price);
            }
            catch (Exception)
            {
                price = price.Replace('.', ',');
                allprices[0] = double.Parse(price);
            }
            currentIndex = data.LastIndexOf('l') + 5;
            string h = data.Substring(currentIndex, 22);
            currentIndex += h.IndexOf(',') + 2;
            i = data[currentIndex];
            price = "";
            do
            {
                price += i;
                currentIndex++;
                i = data[currentIndex];
            } while (i != '.');
            price += '.';
            for (int j = 1; j <= p; j++)
            {
                price += data[currentIndex + j];
            }
            try
            {
                allprices[1] = double.Parse(price);
            }
            catch (Exception)
            {
                price = price.Replace('.', ',');
                allprices[1] = double.Parse(price);
            }

            currentIndex = data.LastIndexOf('h') + 5;
            h = data.Substring(currentIndex, 22);
            currentIndex += h.IndexOf(',') + 2;
            i = data[currentIndex];
            price = "";
            do
            {
                price += i;
                currentIndex++;
                i = data[currentIndex];
            } while (i != '.');
            price += '.';
            for (int j = 1; j <= p; j++)
            {
                price += data[currentIndex + j];
            }
            try
            {
                allprices[2] = double.Parse(price);
            }
            catch (Exception)
            {
                price = price.Replace('.', ',');
                allprices[2] = double.Parse(price);
            }

            return allprices;
        }
